package com.graphic.designer.graphicDesigner.web.user.service;

import com.graphic.designer.graphicDesigner.web.user.model.Avatar;

public class AvatarFactory {

    public static Avatar getDefaultAvatar(){
       Avatar avatar = new Avatar();
       avatar.setName("default-2.jpg");
       avatar.setType("image/jpeg");
       avatar.setBase64("data:image/jpeg;base64,");
       avatar.setSize("3 kB");

       return avatar;
    }
}
